INSERT INTO `incident` VALUES (2, NULL, '2018-12-26', '09:00:00', '6', '16', '1', '7', '84', '1', '3', NULL, 'เจาะเลือดคนไข้ไม่ครบถ้วน', 'ตึกncd', '-คนไข้dm hn25490 ยื่นใบเจาะเลือดสีเขียว เจ้าหน้าที่ห้องแล็บเจาะ fbs ตามใบนัด แล้วให้คนไข้ไปทานข้าว\r\n-พบว่ามีใบนัดเจาะสีขาวตรวจ fbs,lipid ด้วย แต่ผู้ช่วยเหลือคนไข้แยกไว้และส่งให้เจ้าหน้าที่ห้อ', 'แจ้งพยาบาล dm นัดคนไข้เจาะ lipid ครั้งหน้า', 'ควรใช้ใบนัดเจาะเลือดสีขาว ที่ออกจากระบบHosXP และมีข้อมูลครบถ้วน ในการส่งเจาะเลือด เท่านั้น', '74', NULL, NULL, NULL, 'enable', 'Y', '2019-01-22 13:45:55', NULL, NULL, '11', 'Y', '2019-02-27 11:24:00', NULL, 'กรณีมีการนัดหมายต่างๆ ในวันเดียวกัน ควรเย็บใบนัดหมายทุกอย่างไว้ด้วยกัน +อาจ note เพิ่มว่าแพทย์มีสั่ง Lab เพิ่มเติมจากเดิม เพื่อเป็นการสื่อสารที่ชัดเจนยิ่งขึ้น และ N.aid ควรตรวจสอบรายการนัดทุกใบและแนบไปพร้อมกัน', '30', '8', NULL, '2019-01-18 15:20:56', '2019-03-07 13:07:01', NULL, NULL, NULL, 'Y', '11', '2019-03-07 13:07:01', '3', '', '', '');
