INSERT INTO `incident` VALUES (55, NULL, '2018-12-04', '08:30:00', '5', '32', '3', '12', '265', '2', '10', NULL, 'ด้ามกรอเร็ว หลุดออกมาจากซองซีล sterlie', 'ฝ่ายทันตกรรม', 'ผู้ช่วยเตรียมเครื่องมือก่อนเริ่มทำคนไข้  นำด้ามกรอที่มีแสงไฟส่องขณะกรอฟัน ออกมา พบว่า ซองซีลไม่สนิท ทำให้ด้ามกรอร่วงออกมา ตกพื้น  พบว่า ไฟเสีย ไม่สามารถเปิดไฟจากด้ามกรอได้  แต่ลูกปืนในด้ามยัง', 'เตรียมส่งซ่อม\r\nแจ้งผู้ช่วยคนอื่นทราบให้ช่วยกันระมัดระวังมากขึ้น', NULL, '10', NULL, NULL, NULL, 'enable', 'Y', '2019-01-25 09:57:39', NULL, NULL, '11', 'Y', '2019-02-01 11:09:29', NULL, 'ใช้ความระมัดระวังในเครื่องมือและอุปกรณ์ราคาแพง', '49', '74', NULL, '2019-01-23 15:12:23', '2019-03-08 09:42:28', NULL, NULL, NULL, 'Y', '11', '2019-03-08 09:42:28', '3', '', '', '');
INSERT INTO `incident` VALUES (56, NULL, '2018-12-07', '09:00:00', '5', '32', '1', '12', '52', '1', '3', NULL, 'เตรียมเครื่องมือออก รพ.สต. ไม่ครบ', 'รพ.สต. ป่าคาย', 'ผู้ช่วยเตรียม เครื่องมือออกหน่วย ที่รพ.สต.ป่าคาย ไม่ครบ (ขาด ชุดคลุม, ผ้าก๊อซกัดหลังถอนฟัน, root tip pick สำหรับแคะเศษรากฟัน)\r\nซึ่งทพ.ถอนฟันแล้วมีเคสรากฟันหัก ไม่สามารถแคะได้เนื่องจากเครื่องม', 'แจ้งผู้ช่วยที่รับผิดชอบทราบ และทำใบ check list ใหม่ให้ง่ายต่อการจัดของครั้งต่อไป', 'เนื่องจากมีการเปลี่ยนภาระงานของเจ้าหน้าที่ในฝ่าย เนื่องจากมีการโยกย้ายฝ่ายของผู้ช่วย 1  ตำแหน่ง ทำให้ผู้ช่วยที่รับผิดชอบ ยังไม่ชำนาญ จึงเกิดความผิดพลาดในการจัดของ', '140', NULL, NULL, NULL, 'enable', 'Y', '2019-01-25 09:46:18', NULL, NULL, '11', 'Y', '2019-02-01 11:00:31', 'เช็ครายการตามใบ check list ทุกครั้ง', NULL, '49', '74', NULL, '2019-01-23 15:21:22', '2019-03-08 10:02:13', NULL, NULL, NULL, 'Y', '11', '2019-03-08 10:02:13', '3', '', '', '');
