INSERT INTO `incident` VALUES (62, NULL, '2018-05-10', '07:15:00', '6', '24', '1', '11', '131', '2', '13', NULL, 'ป้ายเตียงระบุNPO แต่เจ้าหน้าที่บอกทานได้', 'ผู้ป่วยใน', 'เวลา 7.15 น เจ้าหน้าที่โภชนาการไปแจกอาหารเช้าผู้ป่วยพบว่าป้า่ยหน้าเตียงเป็นNPO แต่เจ้าหน้าที่บอกว่าทานได้', 'แจ้งให้เจ้าหน้าที่ตรวจสอบกับOrder แพทย์', NULL, '129', NULL, NULL, NULL, 'enable', 'Y', '2019-01-25 08:29:45', NULL, NULL, '11', 'Y', '2019-02-28 10:28:17', 'ประสานงานหอผู้ป่วยในทราบ และตรวจสอลOrderแพทย์ก่อนจ่ายอาหาร', NULL, '35', '35', NULL, '2019-01-23 16:18:43', '2019-03-07 10:44:40', NULL, NULL, NULL, 'Y', '11', '2019-03-07 10:44:40', '3', '', '', '');
