INSERT INTO `incident` VALUES (176, NULL, '2019-01-23', '14:45:00', '6', '16', '1', '9', '117', '2', '13', NULL, 'ข้อมูลผู้ป่วยผิดพลาด', 'OPD ส่ง คนไข้ นางสาวสายฝน นาคปนคำ อายุ50ปี มาเอกซเรย์ แต่ประวัติคนไข้ใน HosXP เด้งเตือนว่าคนไข้ตั้งครรภ์ (จะไม่เอกซเรย์ในผู้ป่วยตั้งครรภ์ถ้าไม่มีการเซ็นชื่อรับรองจากแพทย์ว่าจำเป็นต้องทำการเอกซเรย์และเซ็นยินยอมรับทราบจากคน', 'OPD ส่ง คนไข้มาเอกซเรย์ แต่ประวัติคนไข้ใน HosXP เด้งเตือนว่าคนไข้ตั้งครรภ์ (จะไม่เอกซเรย์ในผู้ป่วยตั้งครรภ์ถ้าไม่มีการเซ็นชื่อรับรองจากแพทย์ว่าจำเป็นต้องทำการเอกซเรย์และเซ็นยินยอมรับทราบจากคน', '-สอบถามคนไข้เรื่องการตั้งครรภ์ คนไข้ยืนยันว่าไม่ได้ตั้งครรภ์\r\n-โทรแจ้งและสอบถามOPD', NULL, '133', NULL, NULL, NULL, 'enable', 'Y', '2019-02-15 13:11:52', NULL, NULL, '11', 'Y', '2019-02-27 11:17:40', 'ในวันที่เกิดเหตุการณ์ จนท.เอกซเรย์โทร.มาสอบถามพยาบาลผู้ซักประวัติซ้ำ พยาบาลแจ้งว่าไม่ได้ตั้งครรภ์ แต่บันทึกข้อมูลผิด', 'ก่อนการบันทึกข้อมูลทุกเรื่องควรอ่านย้ำให้ถูกต้องตรงความเป็นจริงก่อน', '30', '80', NULL, '2019-01-31 15:26:41', '2019-03-08 08:49:29', NULL, NULL, NULL, 'Y', '11', '2019-03-08 08:49:29', '3', '', NULL, '');
