INSERT INTO `incident` VALUES (251, NULL, '2019-02-26', '09:30:00', '8', '11', '3', '13', '109', '2', '10', '1', 'ไม่ปิดเครื่องกระตุ้นไฟฟ้าก่อนหยุดการใช้งาน', 'นักกายภาพบำบัดแกะเครื่องกระตุ้นไฟฟ้าออกจากคนไข้ก่อนที่จะหยุดการทำงานของเครื่องมือ ทำให้นักกายภาพได้รับกระแสไฟฟ้าจากเครื่องกระตุ้นไฟฟ้าด้วย', 'นักกายภาพบำบัดแกะเครื่องกระตุ้นไฟฟ้าออกจากคนไข้ก่อนที่จะหยุดการทำงานของเครื่องมือ ทำให้นักกายภาพได้รับกระแสไฟฟ้าจากเครื่องกระตุ้นไฟฟ้าด้วย', 'ปิดเครื่องกระตุ้นไฟฟ้าทันที และตรวจสอบให้ดีทุกครั้งก่อนที่จะนำแผ่นกระตุ้นไฟฟ้าออกจากคนไข้', NULL, '127', NULL, NULL, NULL, 'enable', 'Y', '2019-02-28 09:57:17', NULL, NULL, '11', 'Y', '2019-02-28 11:16:18', NULL, 'รับทราบ', '78', '82', NULL, '2019-02-26 10:25:55', '2019-03-08 09:12:24', NULL, NULL, NULL, 'Y', '11', '2019-03-08 09:12:24', '3', '', NULL, '');
