INSERT INTO `incident` VALUES (253, NULL, '2019-02-25', '10:30:00', '8', '11', '1', '1', '104', '1', '4', '2', 'รอยแดงจากกระเป๋าน้ำร้อน', 'แผนกกายภาพบำบัด', 'ผู้ป่วยแจ้งว่าหลังจากที่มารับการรักษาที่แผนกกายภาพบำบัด กลับบ้านไปพบว่าที่บริเวณหลังมีรอยแดงและตุ่มน้ำใสเกิดขึ้น เนื่องจากการวางแผ่นประคบร้อน และจากการตรวจสอบแล้วพบว่าที่หลังผู้ป่วยมีรอยแดงจร', 'ขอโทษพร้อมทั้งอธิบายให้ผู้ป่วยและญาติเข้าใจถึงสาเหตุของการเกิดการบาดเจ็บ', NULL, '127', NULL, NULL, NULL, 'enable', 'Y', '2019-02-28 10:06:33', NULL, NULL, '11', 'Y', '2019-02-28 11:19:01', 'ทบทวนแนวทางการวางแผ่นประคบร้อนใหม่ \r\nไม่ให้วางแผ่นประคบร้อนพร้อมดึงหลัง', 'รับทราบ', '78', '82', NULL, '2019-02-26 10:52:44', '2019-03-08 09:00:27', NULL, NULL, NULL, 'Y', '11', '2019-03-08 09:00:27', '3', '', NULL, '');
