INSERT INTO `incident` VALUES (256, NULL, '2019-02-06', '10:00:00', '1', '3', '3', '13', '463', '2', '10', NULL, 'หม้อแปลงไฟในรถระเบิด', 'บนรถ refer', 'ขณะไปส่งผป.  หม้อแปลงไฟบนรถเบอร์ 6 ระเบิด ไฟ 220 V  ไม่สามารถใช้ได้ มีประกายไฟเล็กน้อยแต่ไม่มีไฟไหม้ แต่อุปกรณ์ที่ต้องใช้ไฟ  220 V ไม่สามารถใช้ได้ แต่อุปกรณ์มีแบตเตอรี่สำรองเพียงพอ ทำให้สามาร', 'รายงานให้หัวหน้าทราบ\r\nรายงานความเสี่ยง', NULL, '132', NULL, NULL, NULL, 'enable', 'Y', '2019-03-01 15:37:59', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '9', NULL, '2019-02-26 11:03:15', '2019-03-01 15:37:59', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
