INSERT INTO `incident` VALUES (273, NULL, '2019-02-21', '14:00:00', '6', '22', '1', '14', '497', '2', '13', NULL, 'พบเทปกาวติดผ้า', 'เตียงนวดห้องแพทย์แผนไทย', 'พบเทปกาวติดผ้ารองหมอน ผ้าขวางเตียงนอน ติดเสื้อผ้าผู้รับบริการ', 'หยิบออกแล้วนำไปทิ้ง', '1.ผู้ใช้งานอุปกรณ์ที่มีเทปกาวติด ควรแกะเทปทิ้งให้เรียบร้อย\r\n2.ผู้ที่มีหน้าที่ทำความสะอาดเสื้อผ้า ควรตรวจเช็คให้เรียบร้อย\r\n3.เจ้าหน้าที่ในห้องแพทย์แผนไทยควรตรวจสอบก่อนใช้งานทุกครั้ง', '121', NULL, NULL, NULL, 'enable', 'Y', '2019-03-01 10:37:41', NULL, NULL, '11', 'Y', '2019-03-01 15:11:45', 'แจ้งเจ้าหน้าที่ซักฟอกให้ตรวจสอบผ้าก่อนส่งหน่วยงานทุกครั้ง', NULL, '35', '86', NULL, '2019-02-28 14:50:51', '2019-03-07 12:58:40', NULL, NULL, NULL, 'Y', '11', '2019-03-07 12:58:40', '3', '', NULL, '');
