INSERT INTO `incident` VALUES (298, NULL, '2019-03-07', '14:30:00', '1', '7', '1', '14', '78', '2', '10', NULL, 'ผู้พิการรถจักรยานยนต์ล้มขณะขึ้นรถ', 'ทางลาดข้างห้อง ER', 'ได้ยินญาติและผู้ป่วยที่นั่งรออยู่ส่งเสียงดังและมองไปทางลาดข้างห้อง ER จึงเดินไปดูพบว่าผุ้พิการที่มารับบริการ รถจักรยานยนต์ล้ม ไม่มีการบาดเจ็บ สอบถามพบว่าตอนแรกผู้รับบริการนำรถจักรยานยนต์มาจอด', 'เข้าไปช่วยพยุงผุ้ป่วยและรถ สอบถามการบาดเจ็บพบว่าไม่มีการบาดเจ็บ', '1.รปภ.อาจไม่ทราบว่าเป็นรถของผู้พิการเนื่องจากเป็นรถจักรยานยนต์ธรรมดา และนำมาจอดไม่ถูกที่\r\n2.รพ.น่าจะทำที่จอดรถผู้พิการให้ใกล้กับทางขึ้นแผนกผู้ป่วยนอกที่เป็นเส้นทางราบ', '41', NULL, NULL, NULL, 'enable', 'Y', '2019-03-08 10:07:07', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '30', NULL, '2019-03-07 16:20:23', '2019-03-08 10:07:07', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
