INSERT INTO `incident` VALUES (308, NULL, '2019-03-08', '17:20:00', '6', '18', '1', '4', '654', '2', '10', NULL, 'พยาบาลในเวรไปrefer มีemsซ้อน', 'er', 'พยาบาลในเวรบ่ายไปrefer รพ.อุตรดิตถ์ Dx NSTEMI ระหว่างไปrefer มีEMSซ้อน ว.40 ทำให้ต้องใช้เจ้าหน้าที่่ออกเหตุ 2 คน เนื่องจากพยาบาลในเวรไปrefer แล้ว 1 คน ผู้ป่วยรอรับบริการอีก 5 คน มีcase AF', 'ตามพยาบาล IPD ช่วยออก EMS 1 คน', '***ข้อเสนอแนะ ไม่ใช่พยาบาลในเวร ไปrefer ควรใช้พยาบาลนอกเวรไปrefer เพราะเจ้าหน้าที่ห้องอุบัติเหตุฉุกเฉินมีความชำนาญในการออกเหตู และผู้รอรับบริการอีก', '82', NULL, NULL, NULL, 'enable', 'Y', '2019-03-14 12:48:19', NULL, NULL, '11', 'Y', '2019-05-28 17:30:24', 'เสนอปัญหาหัวหน้ากลุ่มการ+หัวหน้างาน เรื่องอัตรากำลัง', NULL, '13', '99', NULL, '2019-03-09 14:41:26', '2019-07-25 09:34:23', NULL, NULL, NULL, 'Y', '11', '2019-07-25 09:34:23', '3', '', NULL, '');
