INSERT INTO `incident` VALUES (335, NULL, '2019-03-20', '14:00:00', '6', '16', '1', '13', '463', '2', '10', NULL, 'คนไข้เดินชนประตูกระจกที่ตึก NCD', 'คนไข้สูงอายุ สายตาไม่ดี เดินมายื่นใบสั่งยาที่ห้องจ่ายยา และขณะเดินไปรอรับยาที่หน้าห้อง  จึงชนประตูกระจกที่ปิดใว้ โดยวันที่20 มีนาคม 62 ได้ทำการเปิดแอร์ให้กับผู้ป่วย จำเป็นต้องปิดประตู', 'คนไข้สูงอายุ สายตาไม่ดี เดินมายื่นใบสั่งยาที่ห้องจ่ายยา และขณะเดินไปรอรับยาที่หน้าห้อง  จึงชนประตูกระจกที่ปิดใว้ โดยวันที่20 มีนาคม 62 ได้ทำการเปิดแอร์ให้กับผู้ป่วย จำเป็นต้องปิดประตู', 'แจ้งผู้ดูแลตึก NCD', 'ควรทำการแก้ไขอย่างแร่งด่วน', '113', NULL, NULL, NULL, 'enable', 'Y', '2019-03-22 14:59:14', NULL, NULL, '11', 'Y', '2019-04-04 15:21:32', NULL, 'แจ้ง NCD ติดป้ายแจ้งเตือนที่ประตูกระจกทุกแห่งที่ NCD เพื่อความปลอดภัยของผู้ป่วยและบุคลากร', '30', '106', NULL, '2019-03-21 14:51:45', '2019-05-03 09:43:40', NULL, NULL, NULL, 'Y', '11', '2019-05-03 09:43:40', '3', '', NULL, '');
