INSERT INTO `incident` VALUES (388, NULL, '2019-04-04', '08:19:00', '2', '8', '1', '11', '481', '2', '10', NULL, 'ไม่ส่งผู้ป่วยไปตรวจเลือดช่วงรอการซักประวัติ', 'OPD', 'HN 7888 มีใบนัดตรวจเลือด ไม่นำใบนัดเจาะเลือดให้ผู้ป่วยไปยื่นเจาะเลือดช่วงรอการซักประวัติ (ที่ OPD card มีใบ Note \"LAB\" สื่อสารติดไว้แล้ว) ทำให้ผู้ป่วยต้องเสียเวลารอผลเลือดนานขึ้น', 'ให้ผู้ป่วยถือใบนัดเจาะเลือดไปยื่นเจาะเลือดที่ห้อง Lab ก่อน แล้วกลับมาซักประวัติหลังเจาะเลือดเรียบร้อยแล้ว', 'การสื่อสารมีชัดเจนแล้วทั้งการปรับรายละเอียดในใบนัดให้ตัวใหญ่ขึ้น และมีการสื่อสารด้วยกระดาษ Note เล็กๆ \"LAB\" แล้ว ควรมีการปฏิบัติตามแนวทางอย่างครบถ้วนเพื่อความรวดเร็วสำหรับผู้รับบริการ', '20', NULL, NULL, NULL, 'enable', 'Y', '2019-04-04 17:07:10', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '30', NULL, '2019-04-04 15:05:27', '2019-04-04 17:07:10', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
INSERT INTO `incident` VALUES (389, NULL, '2019-04-04', '08:35:00', '2', '8', '1', '11', '481', '2', '10', NULL, 'ไม่ส่งผู้ป่วยไปตรวจเลือดช่วงรอการซักประวัติ', 'OPD', 'HN 16178 มีใบนัดตรวจเลือดไม่นำใบนัดตรวจเลือดให้ผู้ป่วยไปยื่นเจาะเลือดช่วงรอการซักประวัติ ทำให้ผู้ป่วยต้องรอผลเลือดนานขึ้น (ที่ OPD card มีใบ Note เล็กสื่อสาร \"LAB\" เสียบเตือนอยู่ด้วยแล้ว)', 'ให้ผู้ป่วยถือใบนัดเจาะเลือดไปยื่นเจาะเลือดที่ห้อง Lab ก่อนกลับมาซักประวัติเมื่อเจาะเลือดเรียบร้อยแล้ว', 'การสื่อสารมีชัดเจนแล้วทั้งการปรับรายละเอียดในใบนัดให้ตัวใหญ่ขึ้น และมีการสื่อสารด้วยกระดาษ Note เล็กๆ \"LAB\" แล้ว ควรมีการปฏิบัติตามแนวทางอย่างครบถ้วนเพื่อความรวดเร็วสำหรับผู้รับบริการ', '20', NULL, NULL, NULL, 'enable', 'Y', '2019-04-04 17:02:36', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '30', NULL, '2019-04-04 15:13:08', '2019-04-04 17:02:36', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
