INSERT INTO `incident` VALUES (414, NULL, '2019-04-14', '13:00:00', '2', '8', '1', '4', '654', '2', '10', NULL, 'นำผู้ป่วยไปเอกซเรย์ และมีคนไข้รอลง Admit และรอค้นบัตรตรวจ', 'er', 'คนไข้ HN 405506 นำผู้ป่วยไปเอกซเรย์ และนำผู้ป่วย Admit  มีคนไข้รอค้นบัตร 5 คน และรอทำบัตรใหม่ 1 คน', 'ให้เจ้าหน้าที่เอกซเรย์มาช่วยเข็นคนไข้ไปเอกซเรย์', 'วันหยุด เสาร์อาทิตย์ ช่วงเวรบ่าย ถึง 20.00 ควรมีเวรเปล ช่วยเข็นคนไข้ไปเอกซเรย์ ช่วยรับคนไข้ ช่วยเข็นไป Admit', '134', NULL, NULL, NULL, 'enable', 'Y', '2019-04-18 11:01:58', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '98', NULL, '2019-04-15 02:04:03', '2019-04-18 11:01:58', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
INSERT INTO `incident` VALUES (415, NULL, '2019-04-13', '09:10:00', '6', '18', '1', '4', '654', '2', '10', NULL, 'ต้องนำคนไข้ไป ADmit และช่วยหัตถการที่ห้องฉุกเฉิน', 'er', 'HN 50181 คนไข้ BP Drop มาช่วยหัตถการที่ห้องฉุกเฉิน และมีคนไข้รอค้นบัตร 7 คน\r\nระหว่างนำคนไข้ ADmit ได้มีคนไข้ HN 34187 มา  ต้องช่วยหัตถการ ส่งแลป เอกซเรย์\r\nทำให้ไม่มีคนไข้ ADmit รอเจ้าหน้าที่ลงไปข้างล่าง และผู้รับบริการรอค้นบัตร', 'ใหเจ้าหน้าที่ รปภ. และแม่บ้าน ช่วยรับคนไข้', 'ให้เพิ่มเจ้าหน้าที่เวรเปลในวันหยุดเสาร์ อาทิตย์ และวันหยุดนักขฤกษ์ ในช่วงเช้า ถึง 20.00 น.', '134', NULL, NULL, NULL, 'enable', 'Y', '2019-04-25 13:30:38', NULL, NULL, '11', 'Y', '2019-05-28 17:13:06', NULL, 'เสนอให้จัดเจ้าหน้าท่ีห้องบัตรในเวรนอกเวลาราชการ โดยเฉพาะในเวรเช้าวันหยุดเสาร์อาทิตย์ และหยุดนักขัตฤกษ์', '13', '98', NULL, '2019-04-15 02:12:47', '2019-07-12 15:53:11', NULL, NULL, NULL, 'Y', '11', '2019-07-12 15:53:11', '3', '', NULL, '');
