INSERT INTO `incident` VALUES (424, NULL, '2019-04-20', '22:33:00', '6', '17', '1', '7', '538', '1', '1', NULL, 'สิ่งส่งตรวจไม่มีชื่อ-สกุลผู้ป่วย', 'ห้องแล็บ', 'IPD ส่งน้ำล้างไตสำหรับเพาะเชื้อ คนไข้ HN26250 ที่ภาชนะสิ่งส่งตรวจไม่ได้ระบุ ชื่อ-สกุล HN คนไข้', 'โทรประสาน IPD ตรวจสอบ', 'ควรตรวจสอบความเรียบร้อย ก่อนนำส่ง', '74', NULL, NULL, NULL, 'enable', 'Y', '2019-04-25 12:54:55', NULL, NULL, '11', 'Y', '2019-09-06 09:47:00', 'แจ้งพยาบาลให้ปฏิบัติตามแนวทางการเก็บสิ่งส่งตรวจ', '**ผู้ช่วยเหลือคนไข้  ให้ตรวจสอบซ้ำก่อนส่งแลป', '38', '8', NULL, '2019-04-20 23:14:11', '2019-09-19 11:18:20', NULL, NULL, NULL, 'Y', '11', '2019-09-19 11:18:20', '3', '', NULL, '');
INSERT INTO `incident` VALUES (425, NULL, '2019-04-19', '16:30:00', '1', '1', '3', '4', '94', '2', '10', '1', 'ส่งหนังสือราชการล่าช้า', 'กลุ่มงานประกันสุขภาพฯ', 'พบหนังสือราชการลงรับวันที่ 10 เมษายน 2562 นำส่งให้กลุ่มงานประกันสุขภาพฯ วันที่ 19 เมษายน 2562 เวลา ประมาณ 16.30 น. จำนวน 3 ฉบับ ดังนี้ 1)หนังสือลงรับที่ 1219วันที่ 10 เม.ย. 62 เวลา 9.17น.เรื่อง แจ้งผลการตรวจสอบเวชระเบียนรวมศูนย์ระดับเขตครั้งที่4 ปี 2562 ลงวันที่ 3 เมษายน 2562  2)หนังสือลงรับที่ 1229วันที่ 10 เม.ย. 62 เวลา 10.18น. เรื่อง แจ้งโอนเงินค่ารักษาพยาบาลผู้ป่วยสิทธิประกันสุขภาพถ้วนหน้าจาก รพ.ค่ายพิชัยดาบหัก ลงวันที่ 3 เม.ย.62\r\n3)หนังสือลงรับที่ 1232วันที่ 10 เม.ย. 62 เวลา 11.43น. เรื่อง แจ้งจัดสรรงบประมาณ ประจำปี2562 จากสสจ.อต.(งบยาเสพติด)ลงวันที่ 10 เม.ย.62', '-', 'ขอความร่วมมือผู้รับผิดชอบงานหนังสือ จัดส่งหนังสือให้หน่วยงานอย่างรวดเร็ว เนื่องจากหากเป็นกรณีหนังสือที่แจ้งให้ดำเนินการในระยะเวลาจำกัด จะทำให้หน่วยงานไม่สามารถดำเนินการได้ทันเวลา', '109', NULL, NULL, NULL, 'enable', 'Y', '2019-04-25 12:55:36', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '71', NULL, '2019-04-22 10:23:50', '2019-04-25 12:55:36', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
