INSERT INTO `incident` VALUES (515, NULL, '2019-05-21', '10:15:00', '5', '32', '3', '11', '131', '1', '2', NULL, 'เตรียมเครื่องมือไม่ถูกต้อง', 'ฝ่ายทันตกรรม', 'ขณะอุดฟันคนไข้ ทันตาภิบาลเรียกให้ผู้ช่วยเตรียมวัสดุอุด GI Fuji IX แต่ผู้ช่วยหยิบวัสดุชนิดอื่นมาแทน ซึ่งไม่ถูกต้อง', 'แจ้งให้ไปเปลี่ยน และหยิบมาใหม่ และทำการพูดคุยว่า ต้องเรียกอย่างไรเพื่อให้สื่อสารเข้าใจตรงกัน', NULL, '9', NULL, NULL, NULL, 'enable', 'Y', '2019-07-12 14:48:31', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '74', NULL, '2019-06-20 13:12:59', '2019-07-12 14:48:31', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
INSERT INTO `incident` VALUES (516, NULL, '2019-06-21', '08:50:00', '2', '9', '4', '10', '644', '2', '10', NULL, 'ข้อมูลหลังจากบันทึกในระบบผิดพลาด', 'OPD', 'HN 8110 มาขอตรวจเลือดไทรอยด์ไปยื่นที่คลินิก ผู้ป่วยต้องชำระเงินเอง พยาบาลบันทึกข้อมูลการส่งตรวจ TFT 3 รายการ สั่ง print ผล Lab มีรายการครบถ้วน แต่เมื่อสั่ง print OPD card พบว่าไม่มีรายการ TFT 3 รายการที่สั่งไป จึงตรวจสอบซ้ำในช่องรายการยา/ค่าบริการ พบว่าไม่มีรายการ TFT ขึ้น', 'แจ้งนักคอมพิวเตอร์มาตรวจสอบ นักคอมพิวเตอร์ตรวจสอบแล้วยังไม่ทราบว่าเกิดจากอะไร ให้แก้ไขเบื้องต้นด้วยการ Key ใหม่ในช่องรายการยา / ค่าบริการไปก่อน....เมื่อส่งรายชื่อผู้ป่วยไปห้องการเงิน ห้องการเงินโทร.กลับมาสอบถามว่าผู้ป่วยมีรายการ TFT ซ้ำ 2 รอบ ... จึงแจ้งให้นักคอมพิวเตอร์ไปตรวจสอบกับห้องการเงิน', NULL, '20', NULL, NULL, NULL, 'enable', 'Y', '2019-07-12 10:57:59', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '30', NULL, '2019-06-21 10:17:44', '2019-07-12 10:57:59', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
