INSERT INTO `incident` VALUES (518, NULL, '2019-06-21', '14:30:00', '2', '8', '4', '9', '613', '2', '13', '1', 'เวชระเบียน(OPD CARD) 2 ฉบับเก็บอยู่ในซองเดียวกัน', 'ห้องบัตร', 'เจ้าหน้าที่ค้น OPD CARD  HN. 3652   แล้วพบ OPD CARD HN. 4121 อยู่ในซองเดียวกัน', 'แจ้งเจ้าหน้าที่ห้องบัตรรับทราบเกี่ยวกับเหตุการณ์ที่เกิดขึ้น  แล้วจัดเก็บเวชระเบียนให้ถูกต้อง', 'ตรวจสอบให้รอบคอบว่าไม่มีเวชระเบียน(OPD CARD) ติดซ้อนกันก่อนใส่ซองทุกครั้ง', '108', NULL, NULL, NULL, 'enable', 'Y', '2019-07-12 10:52:20', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '73', NULL, '2019-06-21 14:41:54', '2019-07-12 10:52:20', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
