INSERT INTO `incident` VALUES (586, NULL, '2019-07-01', '13:00:00', '1', '1', '4', '4', '94', '2', '10', NULL, 'ส่งเอกสารผิดฝ่าย', 'ห้องทันตกรรม', 'เมื่อวันที่ 23 พ.ค. 2562  หนังสือแจ้งโอนเงินจาก สปส.อต. เลขที่ อต 0030/1623  ลว.23 พ.ค. 62  แล้วงานธุรการก็นำไปส่งให้กลุ่มงานทันตกรรมฯ เมื่อวันที่ 30 พ.ค. 2562  และเซ็นรับไป จนงานประกันฯ ได้รับการทวงถามจาก สปส.อต. ว่าทำไมไม่ออกใบเสร็จให้สักที  จึงขึ้นไปติดตามที่งานธุรการ แจ้งว่าไม่มีหนังสือค้างแล้ว แต่ขอตรวจสอบว่าใครรับไปจึงเห็นว่า จนท.งานทันตกรรมเซ็นรับหนังสือไป จึงไปทวงถามหนังสือฉบับนี้มา ปรากฏว่ายังไม่ได้ลงรับและยังไม่มีการเกษียณหนังสือเลย', 'แจ้งให้ สปส.รับทราบว่าได้รับหนังสือแล้ว และจะจัดส่งใบเสร็จรับเงินให้ช่วงไหน เพื่อจะได้ไม่ทวงใบเสร็จอีก และชี้แจงบัญชีว่าทำไมถึงตัดลูกหนี้ล่าช้า', 'ผู้รับและผู้ส่งหนังสือ ควรตรวจสอบหนังสือเวลาเซ็นรับส่งกันว่าถูกต้องหรือไม่ ใช่ของงานตนเองหรือไม่', '21', NULL, NULL, NULL, 'enable', 'Y', '2019-07-12 10:37:06', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '16', NULL, '2019-07-01 16:12:39', '2019-07-12 10:37:06', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
