INSERT INTO `incident` VALUES (622, NULL, '2019-08-14', '10:45:00', '6', '17', '1', '1', '203', '2', '10', '1', 'ชื่ออาหารคนไข้ผิดประเภท', 'โภชนาการ', 'ยอดอาหารผู้ป่วยคีร์มาไม่ตรงตามที่เบิกร์ \r\nยอดเป็นอาหารเฉพาะโรคแต่ตึกคีร์มาเป็นอาหารธรรมดา ซึ่งอาจทำให้อาหารที่ผู้ป่วยได้รับไม่ตรงตามที่แพทย์สั่ง จำนวน 5 ราย 1.นส.บุญชู ชมเชย 2.นายประทิน เหล็กไทย 3. นายงาม ยะสุยา 4.นายด่วน ลีพุ่ม 5.นางทองสี สิงอิน และอาจทำให้ยอดสติกเกอร์ติดถาดอาหารไม่ออกเพราะคีร์ประเภทอาหารมาผิดประเภท', 'โทรแจ้ง จนท.ตึกหอผู้ป่วยเพื่อให้ดำเนินการแก้ไขให้ใหม่', NULL, '103', NULL, NULL, NULL, 'enable', 'Y', '2019-08-23 09:39:50', NULL, NULL, '11', 'Y', '2019-09-05 16:00:56', 'แจ้งผู้ปฏบัติ ให้มีความรอบคอบในการคีย์สั่งอาหารผู้ป่วย', NULL, '38', '69', NULL, '2019-08-14 14:01:24', '2019-09-19 10:39:31', NULL, NULL, NULL, 'Y', '11', '2019-09-19 10:39:31', '3', '', NULL, '');
INSERT INTO `incident` VALUES (623, NULL, '2019-08-14', '15:30:00', '6', '17', '1', '11', '131', '2', '10', '1', 'ประเภทอาหารไม่ตรงหมวดโรค', 'โภชนาการ', 'ใบสั่งอาหารผู้ป่วยในคีย์ประเภทอาหารของผู้ป่วยมาไม่ถูกต้อง ซึ่งคนไข้นางจันทร์เพ็ญ อุ่นเมือง โรคเบาหวานความดันงดมัน แต่เบิกร์เป็นอาหารธรรมดาความจริงควรเบิกร์เป็นอาหารเฉพาะโรค และ นส.บุญชู ชมเชย เบิกร์เป็นอาหาร อ่อน แต่ความจริงเป็นโรคมะเร็ง  แต่พบว่ายอดอาหารที่สั่งเป็นประเภทอาหารธรรมดา - อ่อน ไม่ได้เบิกร์เป็นอาหารเฉพาะโรค ซึ่งความจริง จนท.หอผู้ป่วยในควรคีร์ประเภทอาหารเป็นอาหารเฉพาะโรค ซึ่งอาจทำให้คนไข้ได้รับอาหารไม่ตรงตามที่แพทย์สั่ง', 'โทรแจ้งประสานกับ จนท.หอผู้ป่วยในเพื่อทำการแก้ไขให้ใหม่และถูกต้อง', 'ควรตรวจสอบประเภทอาหารของผู้ป่วยให้ชัดเจนเพื่อความถูกต้อง ประเภทอาหาร หากผู้ป่วยเป็นโรค เบาหวาน ความดัน ไขมัน ไต อื่นๆ ควรคีร์เป็นอาหารเฉพาะโรค แต่หากผู้ป่วยไม่มีโรคควรคีร์เป็นอาหารธรรมดา - อ่อน ตามแพทย์สั่ง', '103', NULL, NULL, NULL, 'enable', 'Y', '2019-08-23 09:38:17', NULL, NULL, '11', 'Y', '2019-09-05 15:55:09', 'แจ้งผู้ปฏิบัติให้มีความรอบคอบในการคีย์สั่งอาหารให้ผู้ป่วย  (ตามข้อเสนอแนะของงานโภชนาการ)', NULL, '38', '69', NULL, '2019-08-14 15:41:25', '2019-09-19 10:40:00', NULL, NULL, NULL, 'Y', '11', '2019-09-19 10:40:00', '3', '', NULL, '');
