INSERT INTO `incident` VALUES (631, NULL, '2019-08-22', '08:55:00', '10', '20', '3', '11', '665', '2', '10', NULL, 'ไม่ลงวันนัดและรายการนัด Lab ในเวชระเบียนและในระบบ HosXP', 'OPD', 'HN 2481 แจ้งว่ามาเจาะเลือดตามนัด ตรวจสอบใน OPD card ไม่พบว่ามีรายการนัด สืบค้นในระบบ Hos XP ก็ไม่มีรายการนัด จึงสอบถามผู้ป่วยแจ้งว่ามาตรวจสุขภาพพนักงานโรงโม่ แล้วมีนัดตรวจเลือดซ้ำวันนี้', 'เปิดดูรายการส่งตรวจ Lab พบว่าวันนี้มีการบันทึกตรวจ LFT, Lipid Profile จึง Note แจ้งแพทย์ทราบ', NULL, '20', NULL, NULL, NULL, 'enable', 'Y', '2019-08-23 09:32:42', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '30', NULL, '2019-08-22 11:01:32', '2019-08-23 09:32:42', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
