INSERT INTO `incident` VALUES (643, NULL, '2019-08-30', '11:20:00', '4', '15', '4', '9', '117', '1', '2', NULL, 'ไม่ได้แก้ไขข้อมูลในเวชระเบียน', 'OPD', 'HN 42792 แพทย์สั่ง admit ขณะกำลังจะ complete ข้อมูลก่อน admit พบว่าเวชระเบียนผู้ป่วยนอก แพทย์มีคำสั่งการรักษาแบบผู้ป่วยนอกและมีนัด F/U 1 สป.', 'สอบถามพยาบาลหลังตรวจ แจ้งว่าแพทย์เปลี่ยนใจ admit หลังจากสั่งการรักษาไปแล้ว  จึงให้ N.AID นำ OPD card ไปให้แพทย์แก้ไขเพิ่มเติม', NULL, '20', NULL, NULL, NULL, 'enable', 'Y', '2019-09-05 09:56:46', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '30', NULL, '2019-08-30 14:50:43', '2019-09-05 09:56:46', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
