INSERT INTO `incident` VALUES (656, NULL, '2019-08-19', '09:30:00', '2', '8', '3', '9', '610', '2', '10', NULL, 'เช็คสิทธิ์การรักษา ในใบนำส่งไม่ถูกต้อง', 'โต๊ะคัดกรองหน้าห้องทันตกรรม', 'คนไข้ HN 21230 มาทำฟัน ในนำส่งแจ้งว่าเป็นสิทธิ์ 30 บาท . เจ้าหน้าที่คัดกรองจึงให้คนไข้เดินไปชำระเงิน 30 บาทก่อนทำฟัน\r\nแต่คนไข้แจ้งว่า ตนเองเป็นสิทธิ์ อสม.  เมื่อเช็คในคอมพิวเตอร์จึงทราบว่า สิทธิ์ในคอม กับ ในใบนำส่ง ไม่ตรงกัน', 'ให้คนไข้นั่งรอทำฟันได้เลย ตามสิทธิ์ อสม.', NULL, '9', NULL, NULL, NULL, 'enable', 'Y', '2019-09-19 09:22:53', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '74', NULL, '2019-09-06 11:14:44', '2019-09-19 09:22:53', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
