INSERT INTO `incident` VALUES (663, NULL, '2019-08-22', '08:50:00', '5', '32', '3', '4', '496', '2', '10', NULL, 'เจ้าหน้าที่จำวันออกหน่วน WCC ผิด ทำให้ไปบริการล่าช้า', 'ฝ่ายทันตกรรม', 'เจ้าหน้าที่ทันตกรรม ลงตารางปฏิบัติงานหน่วย WCC ผิดวัน ทำให้เมื่อถึงวันจริง ไม่มีเจ้าหน้าที่ออกตามเวลาที่กำหนด ฝ่ายส่งเสริมโทรมาตาม จึงรีบแบ่งเจ้าหน้าที่ออกไป', 'แบ่งเจ้าหน้าที่ออกไปที่หน่วยคลินิก WCC ทันที', NULL, '65', NULL, NULL, NULL, 'enable', 'Y', '2019-09-19 09:22:01', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '74', NULL, '2019-09-06 11:37:42', '2019-09-19 09:22:01', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
