INSERT INTO `incident` VALUES (693, NULL, '2019-09-19', '11:30:00', '5', '32', '1', '17', '154', '2', '10', NULL, 'คนไข้เด็ก WCC รอทำรับการสอนแปรงฟันนาน', 'โต๊ะคัดกรองหน้าห้องทันตกรรม', 'เจ้าหน้าที่นัดคนไข้เด็ก WCC ผู้ปกครองมาพารับการเคลือบฟลูออไรด์และสอนแปรงฟันตามนัด แต่เมื่อมาถึงเป็นช่วงบ่าย ซึ่งมีการทำฟันเด็กนักเรียนประมาณ 15 คน ทำให้เคสWCCต้องรอหมอนาน  และเด็กเริ่มร้องงอแง', '-เจ้าหน้าที่โต๊ะคัดกรองอธิบายว่ามีคนไข้นักเรียนมาทำฟันมาก และให้กลับไปก่อน และทำการนัดใหม่โดยออกบัตรนัดใหม่และขอเบอร์โทร.ผู้ปกครองไว้กรณีติดต่อนัดฉุกเฉิน \r\n- การนัดหมายครั้งต่อๆไป อาจแยกวันออกอยากกลุ่มเด็กนักเรียน', NULL, '9', NULL, NULL, NULL, 'enable', 'Y', '2019-10-03 09:15:49', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '74', NULL, '2019-10-01 14:59:05', '2019-10-03 09:15:49', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
