INSERT INTO `incident` VALUES (746, NULL, '2019-10-31', '17:00:00', '1', '4', '1', '19', '574', '2', '10', '1', 'เก็บค่าบริการผู้ป่วยซึ่งเป็นสิทธิ์ที่ไม่ต้องชำระเงิน', 'ห้องการเงิน', 'วันที่ 31/10/62 เวลาประมาน 17:30 น. HN 43924 มารับบริการที่ห้องทันตกรรม ด้วยอาการ เคลือบฟลูออไรด์ เป็นการบริการที่ผู้ป่วยไม่ได้ชำระเงิน ซึ่งงานประกันได้ให้สิทธิ์ \"ทันตกรรมเด็ก\" แต่ห้องการเงินเรียกเก็บเงินผู้ป่วย จึงทำให้ข้อมูลลูกหนี้ผิดพลาด และเสี่ยงต่อการถูกร้องเรียน', NULL, 'อยากให้ จนท.การเงินที่มาขึ้นเวรนอกเวลา ตรวจสอบข้อมูลให้ชัดเจนมากกว่านี้', '138', NULL, NULL, NULL, 'enable', 'Y', '2019-11-14 13:44:11', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '79', NULL, '2019-11-01 14:42:31', '2019-11-14 13:44:11', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
