INSERT INTO `incident` VALUES (751, NULL, '2019-08-29', '10:00:00', '6', '16', '1', '11', '391', '1', '3', NULL, 'แลป', 'OPD', 'HN 33287 นัดเจาะTFT  แต่ไม่ออกใบนัดให้ผู้ป่วย', 'ส่งเจาะTFT', NULL, '73', NULL, NULL, NULL, 'enable', 'Y', '2019-11-14 15:16:56', NULL, NULL, '11', 'Y', '2019-11-19 10:34:38', NULL, 'เมื่อผู้ป่วยออกมาจากห้องตรวจแล้ว พยาบาลหลังตรวจให้ตรวจสอบการนัดหมายของแพทย์ให้ครบถ้วน ทั้งในใบนำส่งและใน OPD card เพราะบางครั้งแพทย์ระบุการนัดในใบนำส่ง บางครั้งระบุใน OPD card  และบางครั้งระบุทั้ง 2 ใบ  และให้พยาบาลบันทึกการนัดหมายต่างๆ ให้ครบถ้วนเพื่อให้เป็นไปตามแผนของแพทย์', '30', '18', NULL, '2019-11-04 13:44:31', '2019-11-19 10:34:38', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
