INSERT INTO `incident` VALUES (779, NULL, '2019-10-24', '14:00:00', '3', '14', '4', '14', '712', '2', '10', NULL, 'สถานที่เก็บยาที่รับจากขนส่งไม่เหมาะสม', 'จุดรับพัสดุด้านยา', 'พบกล่องยาที่รับจากขนส่งที่จุดรับพัสดุชั่วคราว(สถานที่ก่อสร้างห้องกายภาพ) มีฝุ่นหนา และเป็นห้องที่เข้าถึงได้ เสี่ยงต่อการสูญหาย', 'ทำความสะอาดกล่องยา และย้ายไปเก็บไว้ในคลังเวชภัณฑ์ยาจนกว่าจะมีห้องรับพัสดุ', NULL, '112', NULL, NULL, NULL, 'enable', 'Y', '2019-11-15 10:46:32', NULL, NULL, '11', 'Y', '2020-01-16 14:52:45', 'ดำเนินการตรวจรับและย้ายไว้ในคลังทั้งหมด', 'เร่งรัดปรับปรุงห้องตรวจรับให้ใช้งานได้', '3', '3', NULL, '2019-11-14 16:07:24', '2020-01-16 14:52:45', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
INSERT INTO `incident` VALUES (780, NULL, '2019-11-14', '14:00:00', '10', '21', '1', '1', '485', '1', '3', NULL, 'ให้การวินิจฉัยผิดเนื่องจากผู้ป่วยไม่ให้ข้อมูลที่เป็นจริง', 'คลินิกฟ้าใหม่', 'ผู้ป่วย HN50942 เมื่อวันที่4/11/2562มีการตรวจเลือดประจำปีและพบว่ามีระดับFBS สูงโดยพยาบาลได้สอบถามการงดน้ำอาหารแล้วว่างดตั้งแต่วันที่3/11/2562หลัง20.00น.ผู้ป่วยยืนยันว่างดมาทำให้แพทย์วินิจฉัยว่าเป็นDM2และได้จ่ายยาMFM1*1PCให้2WKและนัดเข้าคลนิกDM   ต่อมาวันที่7/11/2562ผู้ป่วยมาแจ้งว่าวันที่4/11/2562ได้ทานอาหารและน้ำมาก่อนเจาะเลือดแต่ไม่ได้แจ้งเจ้าหน้าที่เและไม่ได้ทานยาMFMเลยและได้เจาะFBSซ้ำผลปกติ แพทย์จึงให้งดยาและยกเลิกนัดเข้าคลินิกDM', 'ประสานทีมNCDและงานประกันยกเลิกการวินิจฉัยและประสานงานการเงินปลดล็อดการเงินและประสานแพทย์ลบรายการยาMFMและส่งให้การเงินปิดค่าใช้จ่ายอีกครั้งและประสานผู้ป่วยให้มาเจาะเลือดFBSและA1Cใหม่ในVISITหน้า', NULL, '63', NULL, NULL, NULL, 'enable', 'Y', '2019-11-15 09:33:31', NULL, NULL, '11', 'Y', '2020-04-09 11:02:14', 'sss', 'dddd', '10', '10', NULL, '2019-11-14 16:36:27', '2020-04-09 11:02:14', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
