INSERT INTO `incident` VALUES (788, NULL, '2019-07-12', '10:00:00', '10', '20', '1', '5', '653', '1', '3', '2', 'นับอายุครรภ์ผิด', 'กลุ่มงานบริการปฐมภูมิฯ', 'หญิงตั้งครรภ์ (HN36222) u/s กับแพทย์ เมื่อวันที่ 4 มิ.ย.62 ให้นับอายุครรภ์ตาม lMP และนัด ANC ครั้งต่อไปคือวันที่ 23 ก.ค.62  (เริ่มผิด) 23 ก.ค.62 มา ANC เจ้าหน้าที่นับอายุครรภ์จาก u/s ไม่ได้นับอายุครรภ์ตาม LMP จนถึงปัจจุบัน ตรวจสอบเมื่อวันที่ 12 พ.ย.62 พบว่าคิดอายุครรภ์ผิด', 'นับอายุครรภ์ ให้ใหม่ แก้ในประวัติ (สมุดสีชมพู และใน OPD CARD)', NULL, '17', NULL, NULL, NULL, 'enable', 'Y', '2019-11-15 11:17:46', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '23', NULL, '2019-11-15 09:49:37', '2019-11-15 11:17:46', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
