INSERT INTO `incident` VALUES (799, NULL, '2019-11-18', '09:40:00', '1', '4', '4', '19', '278', '2', '13', NULL, 'ไม่แนบใบส่งรูดบัตรเบิกได้จ่ายตรงเสี่ยงสูญเสียรายได้', 'ตึก NCD', 'ไม่แนบใบรูดบัตรเบิกได้จ่ายตรงมากับ Opd card จำนวน  3 เคส HN 46632 และ HN 15916 และ HN 5257 \r\nเสียงสูญเสียรายได้', 'ดูสิทธิ์  ส่งกลับไปรูดบัตรที่โต๊ะการเงิน แจ้งจนท. แนบบัตรรูดมาด้วย', 'ควรตรวจสอบสิทธิ์พร้อมแนบบัตรหลังจากออกวันนัดผู้ป่วย  แล้วส่งมาเช็คจำนวนยาและราคา เพื่อป้องกันการสูญเสียรายได้', '112', NULL, NULL, NULL, 'enable', 'Y', '2019-11-29 11:27:17', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '105', NULL, '2019-11-18 09:51:43', '2019-11-29 11:27:17', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
