INSERT INTO `incident` VALUES (837, NULL, '2019-12-03', '08:30:00', '7', '12', '1', '1', '578', '1', '3', NULL, 'ผู้ป่วยไม่ได้รับการตรวจLAB ครบตามคำสั่งใบนัดตรวจล่วงหน้า', 'คลินิกฟ้าใหม่', 'ผู้ป่วยที่มารับยาต้านไวรัสวันที่3/12/2562จำนวน15รายและมีการสั่งLAB ไว้ล่วงหน้าไม่ได้รับการตรวจfbsจำนวน15รายคือในใบนัดมีการสั่งไว้แล้วแต่เจ้าหน้าที่ห้องเจาะเลือดดูคำสั่งเฉพาะในระบบคอมฯและไม่ได้ติดต่อกับเจ้าหน้าที่ห้องฟ้าใหม่ว่าคำสั่งไม่ตรงกันส่งให้ผู้ป่วยกลุ่มนั้นไม่ได้รับการตรวจfbsและเจ้าหน้าที่ห้องฟ้าใหม่และแพทย์ก็ไม่ทราบว่าไม่ได้รับการตรวจLABตัวนี้  มาทราบอีกในวันถัดไปตอนนำข้อมูลมาคีย์โปรแกรมNAP ซึ่งไม่สามารถตรวจเพิ่มได้เนื่องจากSERUM ที่เก็บไว้นานเกิน24ชม.', 'ประสานห้องlab หาสาเหตุและให้ช่วยประสานให้เจ้าหน้าที่ห้องLAB แจ้งเจ้าหน้าที่ที่เกี่ยวข้องในห้องLAB ให้ช่วยติดต่อเจ้าหน้าที่ห้องฟ้าใหม่อีกครั้งกรณีการสั่งLABในใบนัดเจาะเลือดล่วงหน้า ไม่ตรงกับในProgam คอมฯเพื่อป้องกันการตรวจเลือดไม่ตรงตามคำสั่งการรักษา และให้เจ้าหน้าที่ห้องฟ้าใหม่และแพทย์ผู้เกี่ยวข้องเพิ่มการตรวจสอบผลLABว่าครบหรือไม่ในวันคลินิก', NULL, '63', NULL, NULL, NULL, 'enable', 'Y', '2020-01-24 11:46:24', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '10', NULL, '2019-12-11 13:51:09', '2020-01-24 11:46:24', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
