INSERT INTO `incident` VALUES (890, NULL, '2019-12-30', '08:45:00', '6', '18', '1', '1', '124', '1', '3', NULL, 'ให้ยาผิดตำแหน่ง', 'งานอุบัติเหตุ-ฉุกเฉิน', 'มีคำสั่งการรักษาออเดอร์ในOPD Card และบนฉลากยาที่ได้รับจากห้องยาให้ Hyoscine 20 mg IM แต่เจ้าหน้าที่ทำการผสมยาเพื่อให้การรักษาทาง IV', 'ตรวจพบความผิดพลาดจากเจ้าหน้าที่พยา่บาลคู่เวรให้ตรวจสอบออเดอร์อีกครั้ง จึงทำให้ยังไม่ได้ฉีดยาให้แก่ผู้ป่วย จากนั้นเบิกยาใหม่เพื่อนำมาฉีดทาง IM', '-ให้ยาตามหลัก 10R ทุกครั้ง\r\n- ไม่ปฏิบัติตามความเคยชินจากออเดอร์ครั้งก่อน', '174', NULL, NULL, NULL, 'enable', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '29', NULL, '2019-12-30 09:25:32', '2020-01-24 10:09:05', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', 'รายงานของท่านบันทึกลงในโปรแกรม Med.Error', '11');
INSERT INTO `incident` VALUES (891, NULL, '2020-01-02', '08:15:00', '6', '16', '3', '9', '17', '1', '1', NULL, 'ไม่ระบุชือผู้ป่วย', 'ห้องฉุกเฉิน', 'เจ้าหน้าที่ OPD ส่งผู้ป่วยมาเช็ดตัวลดไข้ 2 คน เขียนรายละเอียด T ใส่เศษกระดาษมา แต่ไม่ระบุชื่อผู้ป่วย แล้วให้ญาติผู้ป่วยถือเศษกระดาษมายื่นให้เจ้าหน้าที่ที่ER', '-สอบถามชื่อผู้ป่วยจากญาติ', 'เคยแจ้งไปแล้วหลายครั้งเรื่องการส่งผู้ป่วยมาเช็ดตัวลดไข้ ทุกครั้งที่ส่งมาที่ er จะเขียนใส่เศษกระดาษมา และไม่มี opd card และไม่เขียนชื่อมาเลยเกือบทุกครั้ง \r\n-อาจจะต้องมีข้อตกลงร่วมกันว่า ถ้าไม่เขืยนชื่อระบบตัวผู้ป่วยมา เจ้าหน้าที่ er อาจจะต้องขอปฏิเสธรับผู้ป่วยแล้วส่งผู้ป่วยกลับไปที่ OPD ก่อน', '14', NULL, NULL, NULL, 'enable', 'Y', '2020-01-23 13:04:16', NULL, NULL, '11', 'Y', '2020-01-31 11:06:19', NULL, 'ก่อนส่งผู้ป่วยไปทำอะไรต่างๆ ให้ระบุรายละเอียดต่างๆ และชื่อสกุลให้ชัดเจน เพื่อการตรวจสอบการระบุตัวผู้ป่วยอย่างถูกต้องตรงคน', '30', '29', NULL, '2020-01-02 08:52:02', '2020-01-31 11:06:19', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
INSERT INTO `incident` VALUES (892, NULL, '2020-01-02', '11:30:00', '6', '18', '3', '9', '117', '1', '2', NULL, 'ไม่ลงเวลาฉีดยาให้ผู้ป่วยทั้งใน OPD card และใบนำส่ง', 'ห้องจ่ายยา', 'ผู้ป่วยมายื่นรับยา ในใบนำส่งแพทย์เขียนยาฉีด Tramadal 50 mg IV และมีรายการยากลับบ้านใน OPD card ห้องยาจะจ่ายยากลับบ้านและจะให้ยาผู้ป่วยฉีดที่ห้องฉุกเฉิน แต่ผู้ป่วยแจ้งว่ารับยาไปฉีดแล้ว 1ครั้ง ก่อนแต่ตรวจสอบไม่พบการลงเวลาฉีดยาทั้งในใบนำส่งที่เขียนด้วยปากกา และใน OPD card  มีโน๊ตปากกาแดงว่า pt ไปกายภาพ', 'ตรวจสอบการคีย์ยา tramadol inj พบว่าคีย์จากห้องยา น่าจะมารับยาไป stat และ และมีรอยฉีดยาที่ข้อพับผู้ป่วย', 'ควรบันทึกเวลาฉีดยาทุกครั้ง เพื่อป้องกันไม่ให้มีการให้ยาซ้ำ หรือหากเกิดแพ้ยาจะได้ทราบเวลาให้ยาได้', '122', NULL, NULL, NULL, 'enable', 'Y', '2020-01-23 13:03:35', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '103', NULL, '2020-01-02 11:47:11', '2020-01-23 13:03:35', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
