INSERT INTO `incident` VALUES (896, NULL, '2020-01-07', '11:45:00', '6', '18', '3', '15', '598', '1', '2', NULL, 'ไม่ลงเวลาฉีดยาให้ผู้ป่วยในใบนำส่ง  ให้มาเบิกยาฉีดรอบ2', 'ห้องจ่ายยา', 'ผู้ป่วย  HN 42853 แพทย์ตรวจที่ OPD สั่งในใบนำส่ง dimen inj 50 mg iV stat\r\nส่งญาติมารับยาฉีดที่ห้องยา ห้องยาคีย์ยาและจ่ายยา 1 amp ให้ไปฉีดที่ห้องฉุกเฉิน \r\nต่อมาญาติมายื่นใบนำส่งที่มาออเดอร์ฉีด dimen inj ตามเดิม ไม่มีการลงเวลาฉีดยา\r\nห้องยาเรียกญาติมาสอบถามแจ้งว่า ER ให้มาที่ห้องยา และญาติแจ้งว่าฉีดยาแล้ว', 'ห้องยาจึงโทรกลับไปสอบถาม ER  สรุปว่าฉีดยาแล้วแต่เนื่องจากไม่ลงเวลา พยาบาลจึงส่งเข้าใจว่ายังไม่ได้ฉีด ส่งมารับยาอีกรอบ', 'ลงเวลาหลังฉีดยาทันทีเพื่อสื่อสารกับทุกฝ่ายให้เข้าใจตรงกัน ป้องกันการให้ยาซ้ำ', '122', NULL, NULL, NULL, 'enable', 'Y', '2020-01-17 12:34:04', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '103', NULL, '2020-01-07 14:06:02', '2020-01-17 12:34:04', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
INSERT INTO `incident` VALUES (897, NULL, '1925-01-08', '17:30:00', '6', '18', '3', '3', '64', '1', '2', NULL, 'เจ้าหน้าที่วานให้ญาติผู้ป่วยหยิบน้ำยาล้างแผลให้', 'ห้องฉุกเฉิน', 'น้ำยาล้างแผล hibiscrub  ใน set ทำแผลหมด เจ้าหน้าที่พยาบาลต้องการน้ำยาเพิ่ม \r\nจึงวานให้ญาติไปหยิบน้ำยจากรถทำแผล เอามาเติมใน set ทำแผลให้ เมื่อญาติผู้ป่วยหยิบน้ำยามาแล้วก็วางขวดลงไปใน set ทำแผลเลย  ทำให้อุปกรณ์ contaminate ต้องเปลี่ยน set ใหม่ \r\nแล้วเจ้าหน้าที่พยาบาลใช้มือที่สวมถุงมือเปื้อนเลือดมาหยิบขวดน้ำยาออกจาก set', '-เปลี่ยน set ทำแผลใหม่ \r\n- เปลี่ยนขวดน้ำยาใหม่', '- ไม่ควรให้ญาติผู้ป่วยหยิบของให้', '14', NULL, NULL, NULL, 'enable', 'Y', '2020-01-24 11:01:32', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '29', NULL, '2020-01-08 21:50:16', '2020-01-24 11:01:32', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
INSERT INTO `incident` VALUES (898, NULL, '2020-01-08', '20:20:00', '5', '32', '1', '1', '578', '1', '3', NULL, 'ผู้ป่วยมีภาวะความดันโลหิตสูงระหว่างมารับบริการที่ทันตกรรมแต่ไม่ได้ส่งมาพบแพทย์', 'ห้องฉุกเฉิน', 'ผู้ป่วย HN 20519 มารักษาที่ห้องฉุกเฉินด้วยมึนเมาศีรษะ ตาลาย แจ้งว่าไม่มีโรคประจำตัว \r\nวัดความดันพบ BP 182/93 วัดซ้ำอีก 30 นาที BP 172/95 แพทย์จึงมี order ให้ทานยาลดความดัน \r\nสอบถามผู้ป่วย แจ้งว่าเคยความดันสูงช่วงมารับบริการแผนกทันตกรรม   เจ้าหน้าที่พยาบาลจึงทำการดูประวัติ พบบันทึกความดันจากแผนกทันตกรรม Bp เริ่มสูงตั้งแต่ ส.ค. 2562 แต่ไม่เคยได้รับการรักษาเรื่องความดันโลหิตเลย \r\nหลัง  observe 1 ชม. BP 146/71 จึงทำการจำหน่ายผู้ป่วยกลับบ้าน และนัด F/U อีก 2 wk.', '-ให้ยาความลดความดันโลหิตสูง\r\nและนัดมา ดู BP อีก  2 wk', 'กรณีพบว่า ผูัป่วยมีภาวะความดันเกินเกณฑ์มาตราฐาน ควรส่งมารักษา ไม่ว่าหัตถการนั้นจะเป็นเพียงการพิมพ์แบบฟันปลอม ก็ควรส่งผู้ป่วยมาพบแพทย์', '14', NULL, NULL, NULL, 'enable', 'Y', '2020-01-17 12:33:13', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '29', NULL, '2020-01-08 22:02:10', '2020-01-17 12:33:13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
