INSERT INTO `incident` VALUES (901, NULL, '2020-01-09', '09:45:00', '6', '16', '1', '9', '163', '2', '10', NULL, 'งานNCDไม่เปิดVISITในการนัดผู้ป่วย', 'คลินิกพักใจ', 'ผู้ป่วยHN57064 มีนัด2คลินิกคือ1.คลินิกพักใจนัดเลิกสุรา2.HTนัดเจาะเลือดและรับยาแต่มีการเปิดvisitวันที่9/01/2563แค่คลินิกพักใจทำให้ห้องLabดึงQคลินิกพักใจไปเจาะเลือดHTทำให้รายการค่าใช้จ่ายและผลlabHTมาอยู่ในค่าใช้จ่ายของพักใจและเมื่อพักใจจะส่งต่อไปรับยาHTต้องประสานห้องบัตรให้ผู้ป่วยอีกครั้งทำให้การรอตรวจคลินิกHTต้องล่าช้าออกไป', 'ประสานห้องบัตรให้ผู้ป่วยอีกครั้งเพื่อเปิดvisitใหม่และแจ้งเจ้าหน้าที่ตึกผู้ป่วยนอกและคลินิกNCDรับทราบปัญหา', NULL, '63', NULL, NULL, NULL, 'enable', 'Y', '2020-01-17 12:32:12', NULL, NULL, '11', 'Y', '2020-01-31 11:02:14', NULL, 'ควรปฏิบัติตามแนวทางการให้บริการอย่างเคร่งครัด', '30', '10', NULL, '2020-01-09 11:50:39', '2020-01-31 11:02:14', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
