INSERT INTO `incident` VALUES (932, NULL, '2020-01-23', '08:00:00', '6', '16', '1', '7', '699', '1', '2', NULL, 'รายการในสั่งตรวจกับในระบบ HOSxP ไม่ตรงกัน', 'กลุ่มงานเทคนิคการแพทย์ รพ.ทองแสนขัน', 'ใบสั่งตรวจ ไม่มีชื่อ-นามสกุล มีแต่เลข HN.46907 ซึ่งระบุรายการตรวจคือ Hct. ที่เขียนด้วยลายมือ แต่ในระบบ HOSxP สั่งตรวจ CBC', 'สอบถาม OPD เพื่อยืนยัน พบว่าตรวจ CBC', 'ควรปริ้นส์ใบนัดจากระบบคอมให้กับผู้ป่วย เพื่อความชัดเจนถูกต้องของข้อมูล ในการยืนยันตัวต้น', '114', NULL, NULL, NULL, 'enable', 'Y', '2020-01-24 10:04:30', NULL, NULL, '11', 'Y', '2020-01-30 11:59:47', NULL, 'การออกใบนัดควรตรวจสอบรายการนัดตรวจ Lab ให้ถูกต้องตรงกันกับในเวชระเบียน/ใบนำส่ง', '30', '92', NULL, '2020-01-23 13:45:24', '2020-01-30 11:59:47', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
