INSERT INTO `incident` VALUES (934, NULL, '2020-01-03', '10:00:00', '2', '8', '1', '9', '610', '2', '10', NULL, 'เชคสิทธิ์ผู้ป่วย', 'ห้องแพทย์แผนไทย', 'เมื่่อวันที่ 3 มค.63 ผู้ป่วยใช้สิทธิ์ผู้สูงอายุ จนท.ได้ทำการเชคสิทธิ์แล้ว แต่ในระบบขึ้น ค่าธรรมบัตรประกันสุขภาพถ้วนหน้า 30 บ.', 'โทรแจ้งจนท.เชคสิทธิ์ จนท.อธิบายว่า หน้าต่างของจนท.ไม่สามารถมองเห็นว่า มีค่าธรรมเนียม 30 บ. ขึ้นในระบบของผู้ป่วย', '-หาแนวทางกับผู้รับผิดชอบโปรแกรม HosXp', '121', NULL, NULL, NULL, 'enable', 'Y', '2020-01-24 10:03:31', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '86', NULL, '2020-01-23 16:46:09', '2020-01-24 10:03:31', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
INSERT INTO `incident` VALUES (935, NULL, '2020-01-10', '15:30:00', '6', '17', '1', '1', '206', '1', '3', '2', 'ผู้ป่วยไม่มีความพร้อมที่จะฉีด Insulin', 'กลุ่มงานบริการปฐมภูมิฯ', 'กลุ่มงานเภสัชฯ โทรแจ้งให้หมอครอบครัว ติดตามผู้ป่วย HN 19912 ซึ่งเป็นผู้ป่วย DM ที่มา Admit ด้วยน้ำตาลสูง และแพทย์ให้ D/C วันที่ 9 มกราคม 2563 และสั่งฉีด Insulin ผู้ป่วยรับยาที่ห้องยา และเภสัชฯ ได้สอนการฉีด Insulin แก่ผู้ป่วย แต่ผู้ป่วยไม่เข้าใจ และไม่มีญาติมาสอน หอผู้ป่วยในไม่มี D/C plan ก่อนที่จะ D/C เพื่อเตรียมการฉีด Insulin', '- ประธาน อสม. ประจำหมู่บ้าน เพื่อวางแผนการดูแล\r\n- เวลา 16.50 น. หมอประจำครอบครัว ลงหมู่บ้าน ติดตามผู้ป่วยไม่สามารถฉีด Insulin ได้ แต่มีลูกชายและลูกสะใภ้ที่จะฉีดให้', 'วางแผนการจำหน่ายผู้ป่วยกลับบ้าน ( D/C plan)', '17', NULL, NULL, NULL, 'enable', 'Y', '2020-02-13 16:17:32', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '23', NULL, '2020-01-24 15:53:13', '2020-02-13 16:17:32', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
INSERT INTO `incident` VALUES (936, NULL, '2020-01-24', '16:20:00', '6', '16', '3', '7', '698', '2', '13', '1', 'คีย์order lab ผิดพลาด', 'ห้องแล็บ', 'หน่วยงานผู้ป่วยนอกได้สั่งแลป Fasting Blood sagar (ปลายนิ้ว) ซึ่งได้สอบถามคนไข้  คนไข้นั้นกินข้าวมาแล้ว เพราะฉะนั้นหน่วยงานผู้ป่วยนอกจึงต้องคืย์ Order lab เป็น Blood sagar - No NPO ปลายนิ้ว', 'ได้บอกเจ้าหน้าที่ ที่เกี่ยวข้องแล้ว', NULL, '162', NULL, NULL, NULL, 'enable', 'Y', '2020-01-25 10:46:09', NULL, NULL, '11', 'Y', '2020-01-30 12:01:26', NULL, 'ก่อนส่งผู้ป่วยตรวจเลือดที่ต้อง NPO ให้สอบถามก่อนว่าผู้ป่วยว่า NPO มาหรือไม่ เพื่อแก้ไขรายการตรวจให้ถูกต้อง หรืองดส่งตรวจกรณีเป็นนัด Lipid profile', '30', '87', NULL, '2020-01-24 16:27:29', '2020-01-30 12:01:26', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
