INSERT INTO `incident` VALUES (938, NULL, '2020-01-24', '14:53:00', '1', '7', '3', '13', '463', '2', '11', '1', 'บุคคลที่ไม่รู้จักขึ้นห้องพัก', 'แฟลต 2 ชั้น ห้อง 224', 'เวลา 14.50 น.มีชายไม่ทราบชื่อ ซึ่งเจ้าของห้องไม่รู้จักขึ้นไปเปิดประตูห้องพัก เมื่อฃายไม่ทราบชื่อเห็นเจ้าของห้องตกใจจึงปิดประตู เจ้าของห้องจึงรีบไปล็อคประตูด้านนอก ชายไม่ทราบชื่อ ยังคงเคาะประตูอยู่ 2 ครั้ง เมื่อถามว่าเป็นใครมาจากไหน มาหาใคร ชายไม่ทราบชื่อไม่ตอบ และพูดไม่เป็นประโยคไม่สามารถจับใจความได้ จึงโทรหาเพื่อนที่อยู่ ER ให้แจ้งหัวหน้า หัวหน้าให้ รปภ.ไปดูให้พร้อมกับเจ้าหน้าที่อีก 3 คน พบว่าชายไม่ทราบชื่อลงมาอยู่ข้างล่างแฟลต เมื่อเจ้าหน้าที่ถามว่า มาหาใครชายไม่ทราบชื่อแจ้งว่า มาหาสาว หลังจากนั้นก็เดินกลับไปแต่โดยดี', NULL, NULL, '175', NULL, NULL, NULL, 'enable', 'Y', '2020-02-19 12:05:01', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '119', NULL, '2020-01-24 16:57:07', '2020-02-19 12:05:01', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
