INSERT INTO `incident` VALUES (966, NULL, '2020-01-08', '10:00:00', '6', '16', '1', '11', '372', '2', '10', NULL, 'การนัดหมายคนไข้ผิด', 'ห้องพักใจ', 'พบการนัดหมายผิดผู้ป่วยมาบำบัดยาเสพติด เนื่องจากมีวันนัดขึ้นว่า นัดคนไข้มารับยาคลินิกเบาหวาน ซึ่งตัวผู้ป่วยได้เเจ้งว่าไม่ได้มีโรคประจำตัวเป็นเบาหวาน', 'แก้ไขวันนัดหมายใหม่', NULL, '182', NULL, NULL, NULL, 'enable', 'Y', '2020-02-13 19:52:42', NULL, NULL, '11', 'Y', '2020-02-19 11:20:17', NULL, 'การลงบันทึกนัดให้ผู้ป่วย ก่อนกดบันทึกให้ตรวจสอบรายละเอียดต่างๆ ให้ครบถ้วนถูกต้องตามรายการนัด และแผนกที่นัด   พร้อมทั้งตรวจสอบซ้ำก่อนส่งใบนัดให้ผู้ป่วยทุกครั้ง', '30', '75', NULL, '2020-02-11 10:22:42', '2020-02-19 11:20:17', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
