INSERT INTO `incident` VALUES (969, NULL, '2020-02-11', '10:06:00', '2', '8', '1', '9', '524', '1', '3', NULL, 'ไม่แนบ Chart admit มาด้วย', 'OPD', 'HN 35856 แจ้งว่ามาตามนัดหลังจำหน่ายจาก รพ. ไม่มี  Chart แนบมาด้วย', 'ส่งคืนห้องบัตรค้น Chart admit มาให้ ระหว่างรอสอบถามอาการเบื้องต้นจากผู้ป่วย และบันทึกข้อมูลบางส่วนที่สามารถทำได้ (รออยู่ 40 นาทีจีงได้ Chart มา)', 'ถ้าผู้ป่วยมารับบริการแล้ว OPD card อยู่กับ Chart admit ที่รอแพทย์สรุป ไม่ควรดึง Chart admit ออก ให้แนบมาทั้ง OPD card และ Chart admit เนื่องจากต้องดูความต่อเนื่องตามแผนการรักษาของแพทย์ที่แพทย์ระบุวันจำหน่ายด้วย', '20', NULL, NULL, NULL, 'enable', 'Y', '2020-02-13 19:30:33', NULL, NULL, '11', 'Y', '2020-02-24 14:28:06', 'aaa', 'aaa', '71', '30', NULL, '2020-02-11 14:27:54', '2020-02-24 14:28:06', NULL, '7777', '77777', 'Y', '11', '2020-02-24 13:32:11', '2', '', NULL, '');
