INSERT INTO `incident` VALUES (972, NULL, '2020-02-11', '09:45:00', '5', '32', '3', '9', '24', '2', '10', NULL, 'หาchart ผู้ป่วยไม่เจอ', 'ห้องบัตร', 'มีคนไข้ชื่อนางเพลิน ขวานา มาตรวจ เป็นคนไข้admit ค้นเจอopd card แต่ไม่เจอchart ดูที่ห้องหมอกับward หาไม่เจอ ซึ่งคนไข้มาล่าสุด 7 ก.พ.63 ได้มาทำฟัน จึงไปดูที่ห้องฟัน พบchart วางไว้ที่โต๊ะห้องทำฟัน ซึ่งมีแต่opd card กลับมา แต่ไม่มีชาร์ตกลับมา ตั้งแต่วันที่ 7 ก.พ.63', 'ประวัติการมารับบริการล่าสุด คนไข้ได้มาทำฟัน 7 ก.พ.63 จึงไปดู chart ที่ห้องฟัน  เผื่อตกค้างอยู่ที่ห้องฟัน จึงพบchart', 'เวลาไปหาchartจนท.ห้องฟันแจ้งว่าไม่มีชาร์ตตกค้างอยู่ โดยไม่ได้ดูให้ทั่วห้องก่อน ไปตามดูอีกรอบจึงพบชาร์ตวางอยู่ที่โต๊ะห้องทำฟัน ซึ่งจนท.ได้ดึงopd card กับchat แยกออกจากกัน แล้วไม่หนีบคู่กันไว้เหมือนเดิม พอจนท.ที่เอาประวัติไป statt ก็เอากลับไปแต่opd card ไม่มีชารต์กับไปด้วย ควรหนีบchartกับopd card ไว้คู่กันเมื่อใช้เสร็จ ซึ่งชาร์ตวางไว้ที่โต๊ะห้องฟัน ค้างตั้งแต่ 7ก.พ. ซึ่งเป็นเวลาหลายวัน เมื่อเห็นก็ควรส่งคืนห้องบัตร เพราะทำให้เสียเวลาในการค้นหา', '104', NULL, NULL, NULL, 'enable', 'Y', '2020-02-13 13:40:45', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '66', NULL, '2020-02-11 17:37:20', '2020-02-13 13:40:45', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
INSERT INTO `incident` VALUES (973, NULL, '2020-01-31', '17:00:00', '1', '7', '1', '13', '463', '2', '10', NULL, 'ผู้ป่วยออกไปนอกโรงพยาบาล', 'ประตูรั้ว​ รพ.', 'พบเห็นผู้ป่วย​ 2 คน​  สวมชุดผู้ป่วยขับขี่รถมอเตอร์ไซด์​ ผ่านบริเวณจุดตรวจตรงป้อม​ รปภ.​  ออกไปนอกโรงพยาบาล​  ต่อมาสอบถามที่ตึกผู้ป่วยใน​ พบว่าพยาบาลเวรไม่ทราบ​  จนท.ได้สอบถามผูป่วยรายดังกล่าว​ บอกว่า​ ไม่ได้แจ้งพยาบาล​ แต่รปภ.อนุญาติให้ออกไป', NULL, NULL, '172', NULL, NULL, NULL, 'enable', 'Y', '2020-02-13 19:40:05', NULL, NULL, '11', NULL, NULL, NULL, NULL, NULL, '11', NULL, '2020-02-12 10:09:33', '2020-02-13 19:40:05', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '');
INSERT INTO `incident` VALUES (974, NULL, '2020-02-12', '08:35:00', '6', '16', '1', '11', '372', '1', '3', NULL, 'ผู้ป่วยไม่ได้วัดความดันบันทึกมาให้', 'OPD', 'HN 19121 วันนี้มาตามนัด  ซึ่งแพทย์ให้วัดความดันที่บ้าน 2สป.มาให้ดูด้วย ตรวจดูในใบบันทึกความดันพบว่าไม่มีการบันทึกเลยสักวัน สอบถามผู้ป่วย ผู้ป่วยบอกว่าไม่เข้าใจว่าต้องวัดความดันมาด้วย', 'บันทึกแจ้งแพทย์ ให้พบแพทย์ตามนัด (วันนี้มีรับยา DLD ด้วย)', 'ก่อนเสร็จสิ้นบริการ ณ จุดใดๆ ก็ตาม ควรสอบถามความเข้าใจของผู้ป่วยซ้ำก่อน เพื่อให้เป็นไปตามแผนการรักษาของแพทย์', '20', NULL, NULL, NULL, 'enable', 'Y', '2020-02-13 19:29:07', NULL, NULL, '11', 'Y', '2020-02-19 11:19:02', NULL, 'ก่อนเสร็จสิ้นบริการ ณ จุดใดๆ ก็ตาม ควรสอบถามความเข้าใจของผู้ป่วยซ้ำก่อน เพื่อให้เป็นไปตามแผนการรักษาของแพทย์', '30', '30', NULL, '2020-02-12 10:31:37', '2020-02-24 14:31:10', NULL, 'aaa', 'aaaa', 'Y', '11', '2020-02-24 14:31:10', '1', '', NULL, '');
